package com.example.qianfangdemo.Utils;import android.content.Context;import android.content.SharedPreferences;public class LocalData {	private SharedPreferences mSharedPreferences;	private SharedPreferences.Editor mEditor;	private static LocalData data;	private LocalData(Context context) {		mSharedPreferences = context.getApplicationContext().getSharedPreferences("order", 0);		mEditor = mSharedPreferences.edit();	}	public static LocalData getInstance(Context context) {		if (data == null) {			data = new LocalData(context);		}		return data;	}	public void setDate(String name, String value) {		mEditor.putString(name, value);		mEditor.commit();	}	public String getDate(String name) {		return mSharedPreferences.getString(name, "");	}}