package com.example.qianfangdemo.Utils;import java.util.HashMap;import java.util.Map;public class CacheData {	private Map<String, Object> data = null;	private static CacheData instance = new CacheData();	private CacheData() {	}	public static CacheData getInstance() {		return instance;	}	public void setData(String key, Object obj) {		if (data == null) {			data = new HashMap<String, Object>();		}		data.put(key, obj);	}	public Object getData(String key, boolean isCache) {		if (null == data)			return null;		Object obj = data.get(key);		if (!isCache) {			destory();		}		return obj;	}	public void destory() {		data = null;	}}